$(document).ready(function () {
	$(".border_bg .scroll-pane").niceScroll(".border_bg .content_scoller",{touchbehavior:false,autohidemode:false});
	$(".border_bg_2 .scroll-pane").niceScroll(".border_bg_2 .content_scoller",{touchbehavior:false,autohidemode:false});
	$('input[name="screen"]').on('change', function(event) {
		event.preventDefault();
		$('#price').text($(this).data('price'));
	});
	$('input[name="color"]').on('change', function(event) {
		var color = $(this).val();
		if(color == 'gold'){
			$('#product_img').attr('src','https://s.vnecdn.net/shop/i/v26/graphics/ld_ss_t8/Gold.png');
		}
		if(color == 'black'){
			$('#product_img').attr('src','https://s.vnecdn.net/shop/i/v26/graphics/ld_ss_t8/prod.png');
		}
		if(color == 'blue'){
			$('#product_img').attr('src','https://s.vnecdn.net/shop/i/v26/graphics/ld_ss_t8/Blue.png');
		}
	});
	$('form[name="preorder"]').on('submit', function(event) {
		event.preventDefault();
		$.ajax({
			url: base_url + '/ajax/preorder',
			type: 'POST',
			dataType: 'json',
			data: $(this).serialize(),
			success: function(data, textStatus, xhr) {
				$('label.error').text('').hide();
				if (data.susscess) {
					alert('Bạn đã đặt mua thành công, chúng tôi sẽ gửi 1 email thông báo đến địa chỉ email của bạn');
					$('form[name="preorder"] input').val('');
					location.reload();
				} else {
					$.each(data, function(index, val) {
						$('.' + index + '_error').text(val).show();
					});
				}
			},
			error: function(xhr, textStatus, errorThrown) {

			}
		});
		$('form[name="preorder"] input').on('focus', function(event) {
			event.preventDefault();
			$('label.error').text('').hide();
		});
	});
});

$(window).scroll(function() {
 var top_start = $(".gs8-s1").position().top - 55;
  if($(window).scrollTop() >= top_start)
  {    
    $('.gs8-menu').addClass('fixed-top');
    $('.section2 ').addClass('active');
	$('.gs8-mnbtn').show();
  }
  else if ($(window).scrollTop() <= top_start)
  {   
    $('.gs8-menu').removeClass('fixed-top');
	$('.section2 ').removeClass('active');
	$('.gs8-mnbtn').hide();
  }
});
$(function () {	
	// truong hop khi click 
	$('a[href^="#"]').on('click', function (e) {
	e.preventDefault();
	onGoto(this.hash);	
	});
});

function onGoto(hash){
	$(document).off("scroll");  
	var target = hash
	var $target = $(target);
	$('html, body').stop().animate({
	'scrollTop': $target.offset().top - 5
	}, 500, 'swing');
} 


